using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class FabricantesService    {        public List<Fabricantes> List()        {            return new FabricantesBusiness().List();        }        public Fabricantes Get(int idfabricantes)        {            return new FabricantesBusiness().Get(idfabricantes);        }        public Int32 Update(Fabricantes fabricantes)        {            return new FabricantesBusiness().Update(fabricantes);        }        public Int32 Insert(Fabricantes fabricantes)        {            return new FabricantesBusiness().Insert(fabricantes);        }        public Int32 Delete(Int32 idfabricantes)        {            return new FabricantesBusiness().Delete(idfabricantes);        }    }}