using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class ImagensprodutosService    {        public List<Imagensprodutos> List()        {            return new ImagensprodutosBusiness().List();        }        public Imagensprodutos Get(int idimagensprodutos)        {            return new ImagensprodutosBusiness().Get(idimagensprodutos);        }        public Int32 Update(Imagensprodutos imagensprodutos)        {            return new ImagensprodutosBusiness().Update(imagensprodutos);        }        public Int32 Insert(Imagensprodutos imagensprodutos)        {            return new ImagensprodutosBusiness().Insert(imagensprodutos);        }        public Int32 Delete(Int32 idimagensprodutos)        {            return new ImagensprodutosBusiness().Delete(idimagensprodutos);        }    }}