using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class MovimentacoesService    {        public List<Movimentacoes> List()        {            return new MovimentacoesBusiness().List();        }        public Movimentacoes Get(int idmovimentacoes)        {            return new MovimentacoesBusiness().Get(idmovimentacoes);        }        public Int32 Update(Movimentacoes movimentacoes)        {            return new MovimentacoesBusiness().Update(movimentacoes);        }        public Int32 Insert(Movimentacoes movimentacoes)        {            return new MovimentacoesBusiness().Insert(movimentacoes);        }        public Int32 Delete(Int32 idmovimentacoes)        {            return new MovimentacoesBusiness().Delete(idmovimentacoes);        }    }}