using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class NiveisService    {        public List<Niveis> List()        {            return new NiveisBusiness().List();        }        public Niveis Get(int idniveis)        {            return new NiveisBusiness().Get(idniveis);        }        public Int32 Update(Niveis niveis)        {            return new NiveisBusiness().Update(niveis);        }        public Int32 Insert(Niveis niveis)        {            return new NiveisBusiness().Insert(niveis);        }        public Int32 Delete(Int32 idniveis)        {            return new NiveisBusiness().Delete(idniveis);        }    }}