using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class PagamentopedidoService    {        public List<Pagamentopedido> List()        {            return new PagamentopedidoBusiness().List();        }        public Pagamentopedido Get(int idpagamentopedido)        {            return new PagamentopedidoBusiness().Get(idpagamentopedido);        }        public Int32 Update(Pagamentopedido pagamentopedido)        {            return new PagamentopedidoBusiness().Update(pagamentopedido);        }        public Int32 Insert(Pagamentopedido pagamentopedido)        {            return new PagamentopedidoBusiness().Insert(pagamentopedido);        }        public Int32 Delete(Int32 idpagamentopedido)        {            return new PagamentopedidoBusiness().Delete(idpagamentopedido);        }    }}