using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class PedidosService    {        public List<Pedidos> List()        {            return new PedidosBusiness().List();        }        public Pedidos Get(int idpedidos)        {            return new PedidosBusiness().Get(idpedidos);        }        public Int32 Update(Pedidos pedidos)        {            return new PedidosBusiness().Update(pedidos);        }        public Int32 Insert(Pedidos pedidos)        {            return new PedidosBusiness().Insert(pedidos);        }        public Int32 Delete(Int32 idpedidos)        {            return new PedidosBusiness().Delete(idpedidos);        }    }}