using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class PedidoscomissionadosService    {        public List<Pedidoscomissionados> List()        {            return new PedidoscomissionadosBusiness().List();        }        public Pedidoscomissionados Get(int idpedidoscomissionados)        {            return new PedidoscomissionadosBusiness().Get(idpedidoscomissionados);        }        public Int32 Update(Pedidoscomissionados pedidoscomissionados)        {            return new PedidoscomissionadosBusiness().Update(pedidoscomissionados);        }        public Int32 Insert(Pedidoscomissionados pedidoscomissionados)        {            return new PedidoscomissionadosBusiness().Insert(pedidoscomissionados);        }        public Int32 Delete(Int32 idpedidoscomissionados)        {            return new PedidoscomissionadosBusiness().Delete(idpedidoscomissionados);        }    }}