using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class PontoapoioService    {        public List<Pontoapoio> List()        {            return new PontoapoioBusiness().List();        }        public Pontoapoio Get(int idpontoapoio)        {            return new PontoapoioBusiness().Get(idpontoapoio);        }        public Int32 Update(Pontoapoio pontoapoio)        {            return new PontoapoioBusiness().Update(pontoapoio);        }        public Int32 Insert(Pontoapoio pontoapoio)        {            return new PontoapoioBusiness().Insert(pontoapoio);        }        public Int32 Delete(Int32 idpontoapoio)        {            return new PontoapoioBusiness().Delete(idpontoapoio);        }    }}