using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class PrecosService    {        public List<Precos> List()        {            return new PrecosBusiness().List();        }        public Precos Get(int idprecos)        {            return new PrecosBusiness().Get(idprecos);        }        public Int32 Update(Precos precos)        {            return new PrecosBusiness().Update(precos);        }        public Int32 Insert(Precos precos)        {            return new PrecosBusiness().Insert(precos);        }        public Int32 Delete(Int32 idprecos)        {            return new PrecosBusiness().Delete(idprecos);        }    }}