using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class ProdutosService    {        public List<Produtos> List()        {            return new ProdutosBusiness().List();        }        public Produtos Get(int idprodutos)        {            return new ProdutosBusiness().Get(idprodutos);        }        public Int32 Update(Produtos produtos)        {            return new ProdutosBusiness().Update(produtos);        }        public Int32 Insert(Produtos produtos)        {            return new ProdutosBusiness().Insert(produtos);        }        public Int32 Delete(Int32 idprodutos)        {            return new ProdutosBusiness().Delete(idprodutos);        }    }}