using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class TipoadesaoService    {        public List<Tipoadesao> List()        {            return new TipoadesaoBusiness().List();        }        public Tipoadesao Get(int idtipoassociado)        {            return new TipoadesaoBusiness().Get(idtipoassociado);        }        public Int32 Update(Tipoadesao tipoadesao)        {            return new TipoadesaoBusiness().Update(tipoadesao);        }        public Int32 Insert(Tipoadesao tipoadesao)        {            return new TipoadesaoBusiness().Insert(tipoadesao);        }        public Int32 Delete(Int32 idtipoassociado)        {            return new TipoadesaoBusiness().Delete(idtipoassociado);        }    }}