using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class TipomovimentacaoService    {        public List<Tipomovimentacao> List()        {            return new TipomovimentacaoBusiness().List();        }        public Tipomovimentacao Get(int idtipomovimentacao)        {            return new TipomovimentacaoBusiness().Get(idtipomovimentacao);        }        public Int32 Update(Tipomovimentacao tipomovimentacao)        {            return new TipomovimentacaoBusiness().Update(tipomovimentacao);        }        public Int32 Insert(Tipomovimentacao tipomovimentacao)        {            return new TipomovimentacaoBusiness().Insert(tipomovimentacao);        }        public Int32 Delete(Int32 idtipomovimentacao)        {            return new TipomovimentacaoBusiness().Delete(idtipomovimentacao);        }    }}